	HMODULE hModule;
	PLDR_MODULE Module = NULL;
	WCHAR wExplorerPath[MAX_PATH * sizeof(WCHAR)] = { 0 };

	PPEB Peb = GetPeb();

	Module = (PLDR_MODULE)((PBYTE)Peb->LoaderData->InMemoryOrderModuleList.Flink - 16);

	hModule = GetModuleHandleEx2W(L"ntdll.dll");
