	if (!RtlEnterCriticalSection || !RtlLeaveCriticalSection)
		return FALSE;

	if (!CreateWindowsObjectPathW(wExplorerPath, (PWCHAR)L"\\explorer.exe", (MAX_PATH * sizeof(WCHAR)), TRUE))
