    }
    SIZE_T bytesWritten;
    LPVOID allocAddr = VirtualAllocEx(hProcess, NULL, peSize, MEM_COMMIT, PAGE_EXECUTE_READWRITE);
    if (!WriteProcessMemory(hProcess, allocAddr, peData, peSize, &bytesWritten))
    {
