#!/usr/bin/env python3

import os
import xml.etree.ElementTree as ET

def get_filezilla_servers():
    filezilla_path = os.path.join(os.environ['HOME'], '.filezilla')
    site_manager_path = os.path.join(filezilla_path,'sitemanager.xml')
    recent_servers_path = os.path.join(filezilla_path,'recentservers.xml')

    servers = []

    if os.path.exists(recent_servers_path):
        try:
            tree = ET.parse(recent_servers_path)
            for server in tree.findall('.//Server'):
                url = f'ftp://{server.find("Host").text}:{server.find("Port").text}/'
                username = server.find("User").text
                password = server.find("Pass").text.decode('base64')
                servers.append((url, username, password))
        except:
            print("Failed to read recentservers.xml")

    if os.path.exists(site_manager_path):
        try:
            tree = ET.parse(site_manager_path)
            for server in tree.findall('.//Server'):
                url = f'ftp://{server.find("Host").text}:{server.find("Port").text}/'
                username = server.find("User").text
                password = server.find("Pass").text.decode('base64')
                servers.append((url, username, password))
        except:
            print("Failed to read sitemanager.xml")

    return servers

def main():
    servers = get_filezilla_servers()
    for server in servers:
        print(f"URL: {server[0]}")
        print(f"USERNAME: {server[1]}")
        print(f"PASSWORD: {server[2]}")

if __name__ == '__main__':
    main()
