// Create a function to run the system command and get the output
function runCommand(command) {
  // Create a new child process
  const child = spawn(command, { shell: true });

  // Get the output of the child process
  let output = "";
  child.stdout.on("data", (data) => {
    output += data.toString();
  });

  // Wait for the child process to exit
  child.on("exit", (code) => {
    if (code!== 0) {
      console.log(`Command failed with code ${code}`);
      return;
    }

    // Return the output of the command
    return output;
  });
}

// Create a function to find the profile names
function findProfileNames(output) {
  // Use a regular expression to find all profile names
  const profileNames = output.match(/All User Profile\s*:\s*(.+)\r/g);

  // Return the profile names
  return profileNames;
}

// Create a function to find the password for a specific profile
function findPassword(profileName) {
  // Run the command to get the password for the specific profile
  const command = `netsh wlan show profile ${profileName} key=clear`;
  const output = runCommand(command);

  // Use a regular expression to find the password in the output
  const password = output.match(/Key Content\s*:\s*(.+)\r/g);

  // Return the password
  return password;
}

