import socket
import time

def connect_to_target(target_ip, timeout=10):
    s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
    s.settimeout(timeout)
    try:
        s.connect((target_ip, 80))
    except socket.timeout:
        return False
    return True

def send_http_get_request(s):
    request = "GET / HTTP/1.1\r\nHost: {}\r\n\r\n".format(s.getsockname()[0])
    s.sendall(request.encode())
    return s.recv(1024)

while True:
    target_ip = "192.168.1.1"
    if connect_to_target(target_ip):
        response = send_http_get_request(s)
        print(response)
    else:
        print("Connection failed")
    time.sleep(1)
```
