import subprocess

def get_mac_addresses():
    mac_addresses = []
    result = subprocess.run(["arp", "-a"], capture_output=True)
    for line in result.stdout.decode().splitlines():
        if "ether" in line:
            mac_addresses.append(line.split()[3])
    return mac_addresses

def change_mac_address(old_mac_address, new_mac_address):
    subprocess.run(["ifconfig", "eth0", "hw", "ether", new_mac_address])

