// Find and update the module list in PEB
PLIST_ENTRY pListHead = NULL;
PLIST_ENTRY pEntry = NULL;
PLIST_ENTRY pPrevEntry = NULL;
PLDR_DATA_TABLE_ENTRY pLdrEntry = NULL;

// Get the PEB from the current process
PEB peb = GetPeb();

// Get the module list from the PEB
pListHead = &peb->Ldr->InMemoryOrderModuleList;

// Iterate through the module list and find the entry for the module we want to update
pEntry = pListHead->Flink;
while (pEntry!= pListHead)
{
    pLdrEntry = CONTAINING_RECORD(pEntry, LDR_DATA_TABLE_ENTRY, InMemoryOrderLinks);
    if (pLdrEntry->DllBase == (PVOID)BaseAddress)
    {
        // Found the entry for the module we want to update
        break;
    }
    pPrevEntry = pEntry;
    pEntry = pEntry->Flink;
}

if (pLdrEntry!= NULL)
{
    // Update the module entry in the PEB
    pLdrEntry->EntryPoint = (PVOID)RtlGetEntryPoint;
    pLdrEntry->SizeOfImage = RtlGetImageSize(BaseAddress);
    pLdrEntry->CheckSum = RtlGetCheckSum(BaseAddress);
    pLdrEntry->TimeDateStamp = RtlGetTimeDateStamp(BaseAddress);

    // Update the module list in the PEB
    if (pPrevEntry!= NULL)
    {
        pPrevEntry->Blink = pEntry->Flink;
    }
    pEntry->Flink = pListHead->Flink;
    pListHead->Flink = pEntry;
}
