    Optional = (PIMAGE_OPTIONAL_HEADER)&Nt->OptionalHeader;
    SectionHeaderArray = (PIMAGE_SECTION_HEADER)((ULONG_PTR)Nt + sizeof(IMAGE_NT_HEADERS));
    CommandLineString = GetCommandLineW();
    CommandLineStringA = GetCommandLineA();

    for (DWORD dwX = 0; dwX < NumberOfPointers; dwX++)
    {
