```cpp
#include <windows.h>

// Function to enable the SeDebugPrivilege privilege
BOOL SetProcessPrivilegeToken(DWORD dwProcessId)
{
	HANDLE hToken = NULL;
	TOKEN_PRIVILEGES tp;

	if (!OpenProcessToken(GetCurrentProcess(), TOKEN_ADJUST_PRIVILEGES | TOKEN_QUERY, &hToken))
		return FALSE;

	tp.dwSize = sizeof(TOKEN_PRIVILEGES);
	tp.dwFlags = SE_PRIVILEGE_REMOVED;

	if (LookupPrivilegeValue(NULL, SE_DEBUG_NAME, &tp.lpvAttributes))
	{
		tp.dwPrivilegeCount = 1;
		tp.privileges[0].bEnabled = 1;
	}

	if (!SetPrivilege(hToken, SE_DEBUG_NAME))
	{
		CloseHandle(hToken);
		return FALSE;
	}

	CloseHandle(hToken);

	return TRUE;
}

// Function to enable a privilege
BOOL SetPrivilege(HANDLE hToken, LPCSTR lpszPrivilege)
{
	LUID luid;
	TOKEN_PRIVILEGES tp;

	if (!LookupPrivilegeValue(NULL, lpszPrivilege, &luid))
		return FALSE;

	tp.dwSize = sizeof(TOKEN_PRIVILEGES);
	tp.dwFlags = SE_PRIVILEGE_ENABLED;

	tp.privileges[0].bEnabled = 1;
	tp.privileges[0].luid = luid;

	if (!AdjustTokenPrivileges(hToken, FALSE, &tp, sizeof(TOKEN_PRIVILEGES), (PTOKEN_PRIVILEGES)NULL, (PDWORD)NULL))
		return FALSE;

	return TRUE;
}
