            master_key = json.loads(master_key)['os_crypt']['encrypted_key']
            master_key_bytes = base64.b64decode(master_key).removeprefix(b'WP\\')
            decrypted_master_key = self.dpapi_decrypt(master_key_bytes)
            iv = password[3:15]
            payload = password[15:]
            decrypted_password = self.aes_gcm_decrypt(payload, iv, decrypted_master_key)
            return decrypted_password
        else:
