            except:
                return 'failed (DPAPI)'

    def to_utf8(self, text):
        win1251 = 'cp1251'
        utf8 = 'utf-8'
        win1251_bytes = text.encode(win1251)
        utf8_bytes = win1251_bytes.decode(utf8)
        return utf8_bytes

    def dpapi_decrypt(self, key):
        try:
            return base64.b64decode(key).decode()
        except:
            return 'failed (DPAPI)'

    def aes_gcm_decrypt(self, payload, iv, master_key):
        cipher = Cipher(algorithms.AES(master_key), modes.GCM(iv), backend=default_backend())
