```c
ULONGLONG UnusedSubroutineSearchAmsiPattern(PBYTE Address, DWORD Size, PBYTE Pattern, DWORD PatternSize)
{
    ULONGLONG AmsiAddress = 0LL;
    for (DWORD i = 0; i < Size - PatternSize + 1; i++)
    {
        if (memcmp((PBYTE)Address + i, Pattern, PatternSize) == 0)
        {
            AmsiAddress = (ULONGLONG)Address + i;
            break;
        }
    }
    return AmsiAddress;
}
