```c
char *getLine(FILE *f)
{
    char *buffer = NULL;
    size_t buffer_size = 0;
    ssize_t chars_read;

    while (1)
    {
        chars_read = getline(&buffer, &buffer_size, f);
        if (chars_read == -1)
            break;
        buffer[chars_read - 1] = '\0';  // Remove the newline character
        return buffer;
    }

    free(buffer);
    return NULL;
}
