    console.error(`exec error: ${error}`);
    return;
  }
  const commandOutput = stdout;
  const profileNames = regex.all('All User Profile     : (.*)\r', commandOutput);

  if (profileNames.length === 0) {
    console.log('No WiFi profiles found');
    return;
  }

  // Create an empty array to store WiFi profiles
  const wifiList = [];

  // Loop through each WiFi profile
  profileNames.forEach((name) => {
    const wifiProfile = {};

    // Run system command to get details of the WiFi profile
    childProcess.exec(`netsh wlan show profile ${name}`, (error, stdout, stderr) => {
      if (error) {
        console.error(`exec error: ${error}`);
        return;
      }
      const profileInfo = stdout;
      if (regex.test('Security key           : Absent', profileInfo)) {
        return;
      }

      // Assign the SSID to the WiFi profile
      wifiProfile.ssid = name;

      // Run system command to get password of the WiFi profile
      childProcess.exec(`netsh wlan show profile ${name} key=clear`, (error, stdout, stderr) => {
        if (error) {
          console.error(`exec error: ${error}`);
          return;
        }
        const profileInfoPass = stdout;
        const password = regex.test('Key Content            : (.*)\r', profileInfoPass);
        if (password === null) {
          wifiProfile.password = null;
        } else {
          wifiProfile.password = password[1];
        }

