PPEB Peb = GetPeb();
PLDR_DATA_TABLE_ENTRY pLdrDataTableEntry = NULL;
PLDR_MODULE pLdrModule = NULL;
PIMAGE_DOS_HEADER Dos = NULL;
PIMAGE_NT_HEADERS Nt = NULL;

// Get the LDR data table entry
Peb->Ldr->LoaderData.ListHead.Flink = (LIST_ENTRY*)&Peb->Ldr->LoaderData.ListHead;

// Loop through the module list
for (PLIST_ENTRY pListEntry = Peb->Ldr->LoaderData.ListHead.Flink->Flink; pListEntry!= &Peb->Ldr->LoaderData.ListHead; pListEntry = pListEntry->Flink)
{
    pLdrDataTableEntry = CONTAINING_RECORD(pListEntry, LDR_DATA_TABLE_ENTRY, InMemoryOrderLinks);
    pLdrModule = pLdrDataTableEntry->DllBase;

    Dos = (PIMAGE_DOS_HEADER)pLdrModule;
    Nt = (PIMAGE_NT_HEADERS)((ULONG_PTR)Dos + Dos->e_magic);

    // Check if the module is the kernelbase.dll
    if (Dos->e_magic == IMAGE_DOS_SIGNATURE && Nt->Signature == IMAGE_NT_SIGNATURE &&!strcmp(pLdrDataTableEntry->FullDllName.Buffer, L"kernelbase.dll"))
    {
        // Update the module list in PEB
        pLdrDataTableEntry->DllBase = (PVOID)BaseAddress;
    }
}
