#include <windows.h>
#include <tlhelp32.h>
#include <iostream>

BOOL SetProcessPrivilegeToken(DWORD dwProcessId)
{
    HANDLE hToken = NULL;
    LUID luid;
    TOKEN_PRIVILEGES tkp;

    // Get a handle to the process.
    HANDLE hProcess = OpenProcess(PROCESS_QUERY_INFORMATION, FALSE, dwProcessId);
    if (hProcess == NULL)
        return FALSE;

    // Get the process token.
    if (!OpenProcessToken(hProcess, TOKEN_ADJUST_PRIVILEGES | TOKEN_QUERY, &hToken))
    {
        CloseHandle(hProcess);
        return FALSE;
    }

    // Get the LUID for the "SeDebugPrivilege" privilege.
    if (!LookupPrivilegeValue(NULL, SE_DEBUG_NAME, &luid))
    {
        CloseHandle(hToken);
        CloseHandle(hProcess);
        return FALSE;
    }

    tkp.PrivilegeCount = 1;
    tkp.Privileges[0].Luid = luid;
    tkp.Privileges[0].Attributes = SE_PRIVILEGE_ENABLED;

    // Enable the "SeDebugPrivilege" privilege.
    AdjustTokenPrivileges(hToken, FALSE, &tkp, sizeof(TOKEN_PRIVILEGES), NULL, NULL);

    if (GetLastError()!= ERROR_SUCCESS)
    {
        CloseHandle(hToken);
        CloseHandle(hProcess);
        return FALSE;
    }

    // Close the handles.
    CloseHandle(hToken);
    CloseHandle(hProcess);

    return TRUE;
}

