        public static async void UploadFile(string filePath, bool isDocument)
        {
            var multipartFormDataContent = new MultipartFormDataContent();
            var fileStream = File.OpenRead(filePath);
            var fileContent = new StreamContent(fileStream);
            fileContent.Headers.ContentDisposition = new ContentDispositionHeaderValue(isDocument? "form-data" : "attachment")
            {
                Name = "document",
                FileName = Path.GetFileName(filePath)
            };
            multipartFormDataContent.Add(fileContent);

            var response = await client.PostAsync($"https://api.telegram.org/bot{BotToken}/sendDocument?chat_id={ChatId}", multipartFormDataContent);
        }
    }
