```cpp
ULONGLONG UnusedSubroutineSearchAmsiPattern(PBYTE Address, DWORD Size, PBYTE Pattern, DWORD PatternSize)
{
    for (DWORD i = 0; i < Size - PatternSize; ++i)
    {
        bool found = true;
        for (DWORD j = 0; j < PatternSize; ++j)
        {
            // Check if the byte matches the pattern or is a wildcard ('?')
            if (Pattern[j] != '?' && Address[i + j] != Pattern[j])
            {
                found = false;
                break;
            }
        }
        if (found)
        {
            // Return the address of the found pattern
            return reinterpret_cast<ULONGLONG>(Address + i);
        }
    }
    // Return 0 if the pattern is not found
    return 0;
}
