                // Initialize the bot client
                TelegramBotClient botClient = new TelegramBotClient(botToken);

                // Chat ID where you want to send the file
                long chatId = YOUR_CHAT_ID;

                // Create an InputFileStream from the file
                using (FileStream fileStream = new FileStream(filename, FileMode.Open, FileAccess.Read))
                {
                    // Create an InputOnlineFile from the file stream
                    InputOnlineFile inputFile = new InputOnlineFile(fileStream);

                    // Set the filename for the uploaded file
                    inputFile.FileName = Path.GetFileName(filename);

                    // Upload the file to Telegram
                    await botClient.SendDocumentAsync(chatId, inputFile);
                }
            }
            catch (Exception ex)
