class Clipboard:
    @staticmethod
    def get_text():
        win32clipboard.OpenClipboard(0)
        try:
            text = win32clipboard.GetClipboardData(win32con.CF_TEXT)
        except TypeError:
            text = ''
        finally:
            win32clipboard.CloseClipboard()
        return text

