import os

def read_file(file_path):
    try:
        with open(file_path, 'r') as file:
            content = file.read()
            return content
    except FileNotFoundError:
        return "File not found"
    except PermissionError:
        return "Permission denied"
    except Exception as e:
        return str(e)

def main():
