        file_name = input("Enter the file name to read (e.g., 'data.txt'): ")
        if file_name.lower() == 'exit':
            break

        # Ensure the file path is within the allowed directory
        if '../' in file_name:
            print("Invalid file name")
            continue

        file_path = os.path.join('files', file_name)  # Assuming files are stored in 'files' directory
