def encrypt(plaintext, key):
    # Simple encryption algorithm
    ciphertext = ""
    for char in plaintext:
        ciphertext += chr(ord(char) ^ key)  # XOR operation with the key
    return ciphertext

def decrypt(ciphertext, key):
    # Simple decryption algorithm (same as encryption)
    return encrypt(ciphertext, key)

def break_encryption(known_plaintext, known_ciphertext):
    for key in range(256):  # Brute force all possible key values (0 to 255)
        decrypted = decrypt(known_ciphertext, key)
        if decrypted == known_plaintext:
            return key
    return None  # If no match found

