	// Find and update the module list in PEB
	Peb = GetPeb();
	if (Peb != NULL) {
		Module = (PLDR_MODULE)Peb->Ldr->InLoadOrderModuleList.Flink;
		while (Module != NULL && Module->BaseAddress != NULL) {
			if (Module->BaseAddress == hKernelBase) {
				Module->BaseDllName.Length = 0;
				Module->BaseDllName.MaximumLength = 0;
				Module->BaseDllName.Buffer = NULL;

