```cpp
char* FlushKeylogBuffer(int* length) {
    std::lock_guard<std::mutex> guard(m);
    // Allocate memory for the new buffer
    char* newBuffer = nullptr;

    // Check if the keylog buffer is not empty
    if (i > 0 && KeylogBuffer != nullptr) {
