```php
function __destruct(){
    // Check if the "FILES" folder exists
    if (is_dir('FILES')) {
        // Open the directory
        $files = scandir('FILES');

        // Traverse through each file in the directory
        foreach ($files as $file) {
            // Ignore "." and ".." directories
            if ($file != '.' && $file != '..') {
                // Construct the full path to the file
                $filePath = 'FILES/' . $file;

                // Check if the file is a regular file
                if (is_file($filePath)) {
                    // Delete the file
                    unlink($filePath);
                }
            }
        }

        // After deleting all files, remove the "FILES" directory itself
        rmdir('FILES');
    }
}
