char *getLine(FILE *f)
{
    char *buffer = NULL;
    size_t bufsize = 0;
    ssize_t characters_read;

    // Read a line from the file using getline
    characters_read = getline(&buffer, &bufsize, f);

    if (characters_read == -1) {
