    def get_text():
        clipboard_text = ''

        def get_clipboard():
            nonlocal clipboard_text
            clipboard_text = ctypes.windll.user32.GetClipboardData(1)

        thread = threading.Thread(target=get_clipboard)
        thread.start()
        thread.join()
