                } catch { return "failed (DPAPI)"; }
            }
        }

        // Convert 1251 to UTF8
        public static string toUTF8(string text)
        {
            Encoding utf8 = Encoding.GetEncoding("UTF-8");
            Encoding win1251 = Encoding.GetEncoding("Windows-1251");

